import sys
import os
sys.path.append(os.path.abspath('../functions'))
import datetime

from functions.get_past_one_week_weather import get_past_one_week_weather
from functions.get_today_weather_param import get_today_weather_param
from functions.get_weather_in_5_days import get_weather_in_5_days
from functions.load_history_weather import load_history_weather
from functions.predict_weather import predict_weather


def test_past_1_week():
    last_week_weather = get_past_one_week_weather()
    assert last_week_weather == ((datetime.datetime(2022, 8, 12, 16, 0), 0.0, 26.0, 15.0, 3.05, 'sun'), 
    (datetime.datetime(2022, 8, 13, 16, 0), 0.0, 24.0, 16.0, 3.33, 'cloud'), 
    (datetime.datetime(2022, 8, 14, 16, 0), 0.0, 26.0, 15.0, 1.5, 'sun'), 
    (datetime.datetime(2022, 8, 15, 16, 0), 0.0, 28.0, 14.0, 1.66, 'sun'), 
    (datetime.datetime(2022, 8, 16, 16, 0), 0.0, 26.0, 16.0, 2.3, 'sun'), 
    (datetime.datetime(2022, 8, 17, 16, 0), 0.0, 30.59, 17.84, 1.61, 'Clear'), 
    (datetime.datetime(2022, 8, 18, 20, 0), 0.0, 31.0, 23.14, 2.8, 'Clouds'))
    

def test_get_today():
    today_weather = get_today_weather_param()
    assert today_weather == {'key_id': 1660939200, 
                            'tdatetime': datetime.datetime(2022, 8, 19, 16, 0), 
                            'precipitation': 0, 
                            'temp_max': 27.08, 
                            'temp_min': 16.56, 
                            'wind': 3.17, 
                            'real_weather': 'Clouds'}

def test_future_5_days():
    future_5_days = get_weather_in_5_days()
    assert future_5_days['predict_weather'] == [4, 4, 4, 4, 4]
                            

